mapper.insert:
INSERT INTO access.access_log (accessTime, ipAddress, httpMethod, httpStatus, httpClient)
VALUES

    #{log.accessTime}, #{log.ipAddress}, #{log.httpMethod}, #{log.httpStatus}, #{log.httpClient}

mapper.select:
SELECT ipAddress, COUNT(*) AS occ
FROM access.access_log

WHERE (accessTime > #{data.startDate}) AND accessTime < #{data.endDate}

GROUP BY ipAddress
HAVING occ > #{data.threshold}

mapper.insert:
REPLACE INTO access.restricted (ipAddress, restricted, cause)
VALUES

    #{ip.ipAddress}, #{ip.restricted}, #{ip.cause}
